    private void basaEkleActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_basaEkleActionPerformed
        int sayi = Integer.parseInt(jTextField1.getText());
        Eleman yeni = new Eleman(sayi);
        l.basaekle(yeni);
        jLabel2.setText(l.listele());
        jTextField1.setText("");

    }//GEN-LAST:event_basaEkleActionPerformed

    private void bastanSilActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bastanSilActionPerformed
         Eleman sonuc = l.bastansil();
        if(sonuc != null)
            jLabel2.setText(l.listele());
        else
            jLabel2.setText("Liste boş ");

    }//GEN-LAST:event_bastanSilActionPerformed

    private void elemanıSilActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_elemanıSilActionPerformed
        int aranan = Integer.parseInt(jTextField1.getText());
        Eleman sonuc = l.elemansil(aranan);
        if(sonuc != null) 
            jLabel2.setText(l.listele());
        else
            jLabel2.setText("Silinecek eleman bulunamadı");
        jTextField1.setText("");

    }//GEN-LAST:event_elemanıSilActionPerformed

