    private void siraliEkleActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_siraliEkleActionPerformed
         int icerik = Integer.parseInt(jTextField1.getText());
        Eleman yeni = new Eleman(icerik);
        l.siraliekle(yeni);
        jLabel2.setText(l.listele());
        jTextField1.setText("");

    }//GEN-LAST:event_siraliEkleActionPerformed

    private void elemanAraActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_elemanAraActionPerformed
        int aranan = Integer.parseInt(jTextField1.getText());
        Eleman sonuc = l.listeara(aranan);
        if(sonuc != null) {
            if(l.yerBul(aranan) != -1)
                jLabel2.setText("Aranan elemanın yeri: " + l.yerBul(aranan));
        }
        else
            jLabel2.setText("Aranan eleman listede yok");
        jTextField1.setText("");

    }//GEN-LAST:event_elemanAraActionPerformed

